#!/usr/bin/env ruby18 -wKU
require "uri"
require "#{ENV["TM_SUPPORT_PATH"]}/lib/tm/htmloutput"
require "#{ENV["TM_SUPPORT_PATH"]}/lib/tm/markdown"

TextMate::HTMLOutput.show(:title => "Markdown Preview", :sub_title => ENV["TM_FILENAME"]) do |io|
  if File.exists?(ENV["TM_FILEPATH"])
    io << "<base href='file://#{URI.escape ENV["TM_FILEPATH"]}'>\n"
  end

  # Convert to html then make one of the html tags above caret
  # into a link that we scroll down to
  html = TextMate::Markdown.to_html($stdin.read)

  lines = html.split("\n")
  n = [ENV["TM_LINE_NUMBER"].to_i, lines.length].min - 7

  while n > 0 && !lines[n].match(/<(h\d|p|ul|li|blockquote|pre|div|img|code|table|tr)>/i)
    n -= 1
  end

  if n > 0 && m = lines[n].match(/<(h\d|p|ul|li|blockquote|pre|div|img|code|table|tr)>(.*)$/i)
    lines[n] = "<#{m[1]} id=\"scroll_to_here\" >#{m[2]}"
  end

  io << lines.join("\n")
  if ENV["TM_MARKDOWN_MATHJAX"].to_i > 0
      io << %{
        <script type="text/x-mathjax-config">
          MathJax.Hub.Config({
            extensions: ["tex2jax.js"],
            jax: ["input/TeX", "output/HTML-CSS"],
            tex2jax: {
              inlineMath: [ ["$","$"], ["\\\\(","\\\\)"] ],
              displayMath: [ ["$$","$$"], ["\\\\[","\\\\]"] ],
              processEscapes: false
            },
            "HTML-CSS": { availableFonts: ["TeX"] }
          });
        </script>
        <script type="text/javascript" src="http://cdn.mathjax.org/mathjax/latest/MathJax.js?config=TeX-AMS-MML_HTMLorMML">
        </script>
      }
  end
  io << "\n<script>window.location.hash = \"scroll_to_here\";</script>"
end
